mapper.delete:
DELETE FROM t_carkind 


   AND id = #{id} 


   AND car_type_no = #{carTypeNo} 


   AND car_type_name = #{carTypeName} 


   AND transfer_price = #{transferPrice} 


   AND suitcases_num = #{suitcasesNum} 


   AND carryon_num = #{carryonNum}

mapper.select:
SELECT 
FROM t_carkind


   AND id = #{id} 


   AND car_type_no = #{carTypeNo} 


   AND car_type_name = #{carTypeName} 


   AND transfer_price = #{transferPrice} 


   AND suitcases_num = #{suitcasesNum} 


   AND carryon_num = #{carryonNum} 



order by ${orderByStr}

SELECT count(*) FROM t_carkind


   AND id = #{id} 


   AND car_type_no = #{carTypeNo} 


   AND car_type_name = #{carTypeName} 


   AND transfer_price = #{transferPrice} 


   AND suitcases_num = #{suitcasesNum} 


   AND carryon_num = #{carryonNum}
