mapper.select:
SELECT tl.line_no lineNo, line_name lineName, available, duration, line_type_no lineTypeNo, type_name typeName, popular_level popularLevel, ten_p tenP,file_url FROM t_line tl left join t_line_price tlp on tl.line_no = tlp.line_no LEFT JOIN t_linekind tlk on tl.line_type_no = tlk.type_no left join t_pic tp on tl.line_no = tp.line_no
where popular_level != 0 and is_main = 1 order by popular_level desc limit #{limit}
where is_main = 1 order by line_type_no
